import { fromJS } from 'immutable'
import { types } from './actions'

export default function contactReducer(state = {form: {}}, action) {
  switch (action.type) {
    case types.ON_CHANGE_FORM:
      return {...state, form : {...state.form, ...action.param}}
    case types.SET_RESPONSE:
      return {...state, response: action.res}
    default:
      return state
  }
}
