import React from 'react'
import {Motion, spring} from 'react-motion'

const FadeComponent = ({display, children}) =>
      <Motion style={{opacity: spring(display ? 1 : 0)}}>
        {({opacity}) =>
          <div style={{opacity: `${opacity}`}}>
            {children}
          </div>
        }
      </Motion>


export default FadeComponent
