import { fromJS } from 'immutable'
import {types} from './actions'
import { upperFirst } from 'lodash'
import config from 'config'

export default function galleriesListReducer(state = {}, action) {
  switch(action.type) {
    case types.SET_IS_FETCHING:
      return { ...state, isFetching: action.bool}
    case types.SET_GALLERIES_LIST_OBJECT:
      return { ...state, galleriesList: fromJS(action.galleriesList)}
    case types.RAZ_LIST:
      return { ...state, galleriesList: undefined }
    default:
      return state
  }
}
