import { fromJS } from 'immutable'
import { types } from './actions'

export default function navReducer(state = {galleriesPage: 1}, action) {
  switch (action.type) {
    case types.SET_GALLERIES:
      return {...state, galleries: fromJS(action.galleries)}
    case types.SET_GALLERIES_LISTS:
      return { ...state, galleriesLists: fromJS(action.lists)}
    case types.SET_CURRENT_GALLERIES:
      return { ...state, currentGalleries: fromJS(action.galleries)}
    case types.SET_GALLERIES_NOT_IN_LISTS:
      return { ...state, galleriesNotInLists: fromJS(action.galleries)}
    case types.SET_GALLERIES_PAGE:
      return {...state, galleriesPage: action.page }
    default:
      return state
  }
}
