const galleriesSelector = ({navState}) => {
  return navState.galleries !== undefined ? navState.galleries.toJS() : undefined
}

const galleriesListsSelector = ({navState}) => {
  return navState.galleriesLists !== undefined ? navState.galleriesLists.toJS() : undefined
}

const currentGalleriesSelector = ({navState}) => {
  return navState.currentGalleries !== undefined ? navState.currentGalleries.toJS() : undefined
}

const galleriesNotInListsSelector = ({navState}) => {
  return navState.galleriesNotInLists !== undefined ? navState.galleriesNotInLists.toJS() : undefined
}

const currentGallerySelector = state => state.galleryState

const galleriesPageSelector = ({navState}) => navState.galleriesPage


export {
  galleriesSelector,
  galleriesListsSelector,
  currentGalleriesSelector,
  currentGallerySelector,
  galleriesNotInListsSelector,
  galleriesPageSelector
}
