import { types } from './actions'

export default function adminReducer(state = {}, action) {
  switch (action.type) {
    case types.SET_MESSAGE:
      return { ...state, message: action.message }
    case types.SAVE_ADRESS_SERVER:
      return { ...state, adressServer: action.adress }
    case types.SET_GALLERIES:
      return { ...state, galleries: action.galleries }
    case types.SET_CURRENT_GALLERY:
      return { ...state, currentGallery: action.gallery }
    case types.SET_GALLERIES_LISTS:
      return { ...state, galleriesLists: action.galleriesLists }
    case types.SET_CURRENT_GALLERIES_LIST:
      return { ...state, currentGalleriesList: action.galleriesList }
    default:
      return state
  }
}
