import { types } from './actions'

export default function adminReducer(state = {
  progressValue: 0,
  uploadSizeValue: 0
}, action) {
  switch (action.type) {
    case types.SET_MESSAGE:
      return { ...state, message: action.message }
    case types.SET_GALLERIES:
      return { ...state, galleries: action.galleries }
    case types.SET_CURRENT_GALLERY:
      const galleries = state.galleries.slice()
      const index = galleries.findIndex(gallery => gallery.id === action.gallery.id)
      galleries[index] = action.gallery
      return { ...state, currentGallery: action.gallery, galleries }
    case types.SET_GALLERIES_LISTS:
      return { ...state, galleriesLists: action.galleriesLists }
    case types.SET_CURRENT_GALLERIES_LIST:
      return { ...state, currentGalleriesList: action.galleriesList }
    case types.SET_PROGRESS_UPLOAD:
      return { ...state, progressValue: action.progressValue }
    case types.SET_UPLOAD_SIZE:
      return { ...state, uploadSizeValue: action.uploadSizeValue }
    default:
      return state
  }
}
