import { types } from './actions'

export default function adminReducer(state = {}, action) {
  switch (action.type) {
    case types.SET_MESSAGE:
      return { ...state, message: action.message }
    case types.SAVE_ADRESS_SERVER:
      return { ...state, adressServer: action.adress }
    case types.SET_GALLERIES:
      return { ...state, galleries: action.galleries }
    case types.SET_CURRENT_PICTURES:
      return { ...state, currentPictures: action.pictures }
    default:
      return state
  }
}
