// import { createSelector } from 'reselect'

const imgsSelector = ({ galleryState }) => returnImmutableObject(galleryState.imgs)
const nbImgsSelector = ({ galleryState }) => galleryState.nbImgs
const currentImageSelector = ({ galleryState }) => galleryState.currentImage
const isFetchingSelector = ({ galleryState }) => galleryState.isFetching
const gallerySelector = ({ galleryState }) => returnImmutableObject(galleryState.gallery)
const returnImmutableObject = (ImmutableObject) => ImmutableObject ? ImmutableObject.toJS() : undefined

export {
  imgsSelector,
  nbImgsSelector,
  currentImageSelector,
  isFetchingSelector,
  gallerySelector
}
